package week1.day1;

public class ControlForNegativenumberToPositivenumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = -10;
		if (i < 0) {
			System.out.println("Positive of " + i + " is " + (-i));
		} else {
			System.out.println(i + " is Positive Number only");
		}
	}

}